    let mut lines = stdin.lock().lines();

    if let Some(Ok(line)) = lines.next() {
        let x: i32 = line.trim().parse().unwrap();
        let result = Solution::is_palindrome(x);
        print!("{}", if result { "true" } else { "false" });
