#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    char line[10000];
    if (fgets(line, sizeof(line), stdin)) {
        char s[2000];
        int sSize = 0;
        char *token = strtok(line, ",\n");
        while (token != NULL) {
            if (strlen(token) > 0) {
                s[sSize++] = token[0];
            }
            token = strtok(NULL, ",\n");
        }

        reverseString(s, sSize);

        printf("[");
        for (int i = 0; i < sSize; i++) {
            printf("\"%c\"%s", s[i], (i < sSize - 1) ? ", " : "");
        }
        printf("]\n");
    }
    return 0;
}

