#include <iostream>
#include <vector>
#include <sstream>
#include <string>

using namespace std;

int main() {
    string line;
    if (getline(cin, line)) {
        vector<char> s;
        stringstream ss(line);
        string segment;
        while(getline(ss, segment, ',')) {
            if (!segment.empty()) s.push_back(segment[0]);
        }

        Solution sol;
        sol.reverseString(s);

        cout << "[";
        for(size_t i = 0; i < s.size(); ++i) {
            cout << "\"" << s[i] << "\"" << (i < s.size() - 1 ? ", " : "");
        }
        cout << "]" << endl;
    }
    return 0;
}

