use std::io::{self, BufRead};

fn main() {
    let stdin = io::stdin();
    let mut lines = stdin.lock().lines();

    if let Some(Ok(line)) = lines.next() {
        let mut s: Vec<char> = line.split(',')
            .map(|x| x.chars().next().unwrap())
            .collect();

        Solution::reverse_string(&mut s);

        print!("[");
        for (i, c) in s.iter().enumerate() {
            if i > 0 { print!(", "); }
            print!("\"{}\"", c);
        }
        print!("]");
    }
}
