#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    char line1[10000];
    char line2[100];
    if (fgets(line1, sizeof(line1), stdin) && fgets(line2, sizeof(line2), stdin)) {
        int nums[1000];
        int numsSize = 0;
        char *token = strtok(line1, ",\n");
        while (token != NULL) {
            nums[numsSize++] = atoi(token);
            token = strtok(NULL, ",\n");
        }

        int target = atoi(line2);
        int returnSize;
        int* result = twoSum(nums, numsSize, target, &returnSize);

        printf("[");
        for (int i = 0; i < returnSize; i++) {
            printf("%d%s", result[i], (i < returnSize - 1) ? ", " : "");
        }
        printf("]\n");
        free(result);
    }
    return 0;
}

