    let mut lines = stdin.lock().lines();

    if let Some(Ok(line1)) = lines.next() {
        let nums: Vec<i32> = line1.split(',')
            .map(|s| s.trim().parse().unwrap())
            .collect();

        if let Some(Ok(line2)) = lines.next() {
            let target: i32 = line2.trim().parse().unwrap();

